import React, { useMemo } from "react";

type Star = {
  x: number;   // 0–100 (vw)
  y: number;   // 0–100 (vh)
  size: number; // px
  delay: number; // s
  tw: number;   // twinkle duration s
  fl: number;   // float duration s
  dx: number;   // px
  dy: number;   // px
  hue: number;  // 0–360
};

export default function BackgroundStars({ count = 30 }: { count?: number }) {
  const stars: Star[] = useMemo(
    () =>
      Array.from({ length: count }).map(() => ({
        x: Math.random() * 100,
        y: Math.random() * 100,
        size: 4 + Math.random() * 6,         // 4–10px
        delay: Math.random() * 4,             // 0–4s
        tw: 2.5 + Math.random() * 2.5,        // 2.5–5s
        fl: 10 + Math.random() * 10,          // 10–20s
        dx: (Math.random() - 0.5) * 40,       // -20–20px
        dy: (Math.random() - 0.5) * 40,       // -20–20px
        hue: 320 + Math.random() * 40,        // pastel róż/fiolet (320–360)
      })),
    [count]
  );

  return (
    <div className="stars-layer" aria-hidden="true">
      {stars.map((s, i) => (
        <div
          key={i}
          className="star"
          style={
            {
              // pozycja
              "--x": `${s.x}vw`,
              "--y": `${s.y}vh`,
              // rozmiar + animacje
              "--size": `${s.size}px`,
              "--delay": `${s.delay}s`,
              "--tw": `${s.tw}s`,
              "--fl": `${s.fl}s`,
              "--dx": `${s.dx}px`,
              "--dy": `${s.dy}px`,
              // kolor w HSL (pastel)
              "--h": s.hue,
            } as React.CSSProperties
          }
        />
      ))}
    </div>
  );
}
