import { useState } from "react";
import styles from "../css/ProjectWindow.module.css";
import { Tag } from "./Tag";
import { Play } from "lucide-react";
import projectsData from "../data/projects.json";

export function ProjectWindow() {
  const [currentProjectIndex, setCurrentProjectIndex] = useState(0);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const project = projectsData[currentProjectIndex];
  const images = project.images || [];

  // --- obsługa zmiany zdjęcia ---
  const nextImage = () => {
    setCurrentImageIndex((prev) => (prev + 1) % images.length);
  };

  const previousImage = () => {
    setCurrentImageIndex((prev) =>
      prev === 0 ? images.length - 1 : prev - 1
    );
  };

  // --- obsługa zmiany projektu ---
  const nextProject = () => {
    setCurrentProjectIndex((prev) => (prev + 1) % projectsData.length);
    setCurrentImageIndex(0); // reset zdjęcia przy zmianie projektu
  };

  const previousProject = () => {
    setCurrentProjectIndex((prev) =>
      prev === 0 ? projectsData.length - 1 : prev - 1
    );
    setCurrentImageIndex(0);
  };

  return (
    <div className={styles.ProjectWindowContainer}>
      <p className={styles.ProjectTitle}>{project.title}</p>

      {/* --- Sekcja zdjęć --- */}
      <div className={styles.ImagesContainer}>
        <Play
          size={50}
          style={{ transform: "scaleX(-1)" }}
          className={styles.ImageButton}
          onClick={previousImage}
        />

        <div className={styles.TempImgDiv}>
          <img
            src={images[currentImageIndex]}
            alt={`Screenshot ${currentImageIndex + 1}`}
            className={styles.ProjectImage}
          />
        </div>

        <Play
          size={50}
          className={styles.ImageButton}
          onClick={nextImage}
        />
      </div>

      {/* --- Tagi --- */}
      <div className={styles.TagsContainer}>
        {project.tags.map((tag, index) => (
          <Tag key={index} label={tag} />
        ))}
      </div>

      {/* --- Opis (scrollowalny) --- */}
      <div className={styles.DescriptionContainer}>
        <p className={styles.DescriptionText}>{project.longdesc}</p>
      </div>

      {/* --- Nawigacja między projektami --- */}
      <div className={styles.AnotherProjectsContainer}>
        <Play
          size={50}
          style={{ transform: "scaleX(-1)" }}
          className={styles.ImageButton}
          onClick={previousProject}
        />
        <Play
          size={50}
          className={styles.ImageButton}
          onClick={nextProject}
        />
      </div>
    </div>
  );
}
