import { useState } from "react";
import styles from "../css/ProjectsSection.module.css";
import projectsData from "../data/projects.json";
import { ProjectCard } from "./ProjectCard";
import { ProjectWindow } from "./ProjectWindow";

export function ProjectsSection() {
  const [openProjectIndex, setOpenProjectIndex] = useState<number | null>(null);

  const openProject = (index: number) => setOpenProjectIndex(index);
  const closeProject = () => setOpenProjectIndex(null);

  const nextProject = () => {
    setOpenProjectIndex((prev) =>
      prev === null ? 0 : (prev + 1) % projectsData.length
    );
  };

  const previousProject = () => {
    setOpenProjectIndex((prev) =>
      prev === null ? 0 : prev === 0 ? projectsData.length - 1 : prev - 1
    );
  };

  return (
    <section id="projects" className={styles.ProjectsSection}>
      <h2 className={styles.SectionTitle}>âœ¦ Projects</h2>
      <div className={styles.CardsGrid}>
        {projectsData.map((project, index) => (
          <ProjectCard
            key={index}
            image={project.images}
            title={project.title}
            description={project.description}
            tags={project.tags}
            onClick={() => openProject(index)} // ðŸ‘ˆ otwiera okno
          />
        ))}
      </div>

      {openProjectIndex !== null && (
        <ProjectWindow
          projectIndex={openProjectIndex}
          projects={projectsData}
          onClose={closeProject}
          onNext={nextProject}
          onPrev={previousProject}
        />
      )}
    </section>
  );
}
