import React from 'react';
import styles from '../css/Menu.module.css';


export function Menu() {
  
    const menuItems = document.querySelectorAll(styles.MenuButton);
    const blob = document.querySelector(styles.Blob);

    menuItems.forEach((item) => {
    item.addEventListener("click", (e) => {
        const rect = e.target.getBoundingClientRect();
        blob.style.left = `${rect.left + rect.width / 2 - 30}px`;
        blob.style.width = `${rect.width + 20}px`;
    });
    });

  function clickMenuItem() {

  }
  
  
  
  return (
    <div className={styles.Menu}>
        <div>
            <button className={styles.MenuButton}>About |</button>
            <button className={styles.MenuButton}>Projects |</button>
            <button className={styles.MenuButton}>Skills |</button>
            <button className={styles.MenuButton}>Contact</button>
        </div>
        
        <svg viewBox="0 0 200 200" xmlns="http://www.w3.org/2000/svg" className={styles.Blob}>
  <path fill="#FF0066" d="M40.4,-25.7C54.4,-14.6,69.3,2.3,64.2,10.6C59.1,18.9,34,18.6,16.3,22.8C-1.3,27.1,-11.4,35.8,-21.1,34.6C-30.9,33.5,-40.4,22.4,-40.8,11.8C-41.3,1.2,-32.6,-9,-24.3,-18.7C-16,-28.5,-8,-37.8,2.6,-39.9C13.2,-41.9,26.4,-36.7,40.4,-25.7Z" transform="translate(100 100)" />
</svg>
    </div>
  );
}
